package week1.day2;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int version=10;
		int a=version;
		if(a>0) {
			System.out.println(a+" is positive");
		}
		else if(a<0) {
			System.out.println(a+" is negative");
		}
		else {
			System.out.println(a+" is zero");
		}

	}

}
